<template>
  <img ref="img" :class="['o-image', {'has-bg': hasBg}]" @error="nofind" :src="src" :alt="alt">
</template>

<script>
export default {
  name: 'o-image',
  props: {
    src: {
      type: String,
      required: true
    },
    alt: {
      type: String
    }
  },
  data () {
    return {
      hasBg: true,
      imgData: 'data:image/png;base64,'
    }
  },
  mounted () {
    this.$refs.img.addEventListener('load', this.clearBg);
  },
  beforeDestroy () {
    this.$refs.img.removeEventListener('load', this.clearBg);
  },
  methods: {
    clearBg () {
      this.hasBg = false;
    },
    nofind (e) {
      const _this = e.target;
      _this.src = this.imgData;
    }
  }
}
</script>

<style scoped>
.o-image{
  object-fit: cover;
  background-position: center;
  background-size: contain;
  background-repeat: no-repeat;
  background-color: #f8f4f3;
}

.o-image.has-bg {
  background-image: url(data:image/png;base64,);
}
</style>
